-- ----------------------------
{
  INSERT INTO `t_penyakit` VALUES 
  ('9', 'Ambeien', 'Ambeien atau wasir adalah pembengkakan atau peradangan pembuluh darah di ujung usus besar (rektum) dan anus. Kondisi ini disebabkan oleh peningkatan tekanan pembuluh darah di sekitar anus. Salah satunya karena mengejan terlalu keras.', '0.0', 'https://www.alodokter.com/ambeien'),

  ('10', 'Batuk Berdarah', 'Hemoptisis atau batuk berdarah adalah batuk berdahak yang mengandung darah. Darah ini dapat berasal dari hidung, tenggorokan, batang tenggorok, dan paru-paru.', '30.0', 'https://www.alodokter.com/batuk-darah'),

  ('11', 'Cacing Hati', 'Cacing hati adalah infeksi pada hati yang disebabkan oleh parasit berupa cacing berbentuk pipih.', '1.0', 'https://www.alodokter.com/cacing-hati'),
  ('12', 'Dermatitis', 'Dermatitis adalah peradangan atau iritasi di kulit yang umumnya ditandai dengan kulit gatal, kering, dan kemerahan. Penyakit kulit ini umumnya terjadi akibat reaksi alergi.', '0.0', 'https://www.alodokter.com/dermatitis'),

  ('13', 'Ebola', 'Ebola adalah penyakit mematikan akibat infeksi virus. Penyakit ini bisa menyebabkan kerusakan organ serta perdarahan di dalam tubuh penderitanya. ', '90.0', 'https://www.alodokter.com/ebola'),

  ('14', 'Flu Burung', 'Flu burung adalah penyakit yang disebabkan oleh infeksi virus influenza tipe A yang ditularkan oleh unggas ke manusia. Ada banyak jenis virus flu burung, tetapi hanya beberapa yang dapat menyebabkan infeksi pada manusia.', '3.0', 'https://www.alodokter.com/flu-burung'),

  ('15', 'Gagal Jantung', 'Gagal jantung adalah kondisi ketika jantung melemah sehingga tidak mampu memompa darah yang cukup ke seluruh tubuh. ', '38.7', 'https://www.alodokter.com/gagal-jantung'),
  ('16', 'Gangguan Kepribadian', 'Gangguan kepribadian adalah salah satu jenis penyakit mental. Kondisi ini menyebabkan penderitanya memiliki pola pikir dan perilaku yang tidak normal dan sulit untuk diubah. ', '0.0', 'https://www.alodokter.com/gangguan-kepribadian'),

  ('17', 'Gondongan', 'Gondongan adalah peradangan kelenjar ludah di bagian samping wajah (parotis) akibat infeksi virus. Kondisi ini ditandai dengan pembengkakan pada sisi wajah di bawah telinga.', '0.0', 'https://www.alodokter.com/gondongan'),

  ('18', 'Kolera', 'Kolera adalah diare yang disebabkan oleh infeksi bakteri Vibrio cholerae. Penyakit ini dapat terjadi pada orang dewasa atau anak-anak.', '84.0', 'https://www.alodokter.com/kolera'),
  ('19', 'Kolesterol Tinggi', 'Kolesterol tinggi adalah kondisi ketika kadar kolesterol di dalam darah melebihi batas normal. Jika dibiarkan, kolesterol yang berlebihan bisa menumpuk di pembuluh darah sehingga meningkatkan risiko terjadinya penyakit.', '7.0', 'https://www.alodokter.com/kolesterol-tinggi'),

  ('20', 'HIV dan AIDS', 'HIV (human immunodeficiency virus) adalah virus yang merusak sistem kekebalan tubuh dengan menginfeksi dan menghancurkan sel CD4.', '67.2', 'https://www.alodokter.com/hiv-aids'),

  ('21', 'Demam', 'Demam adalah meningkatnya suhu tubuh hingga lebih dari 380C. Kondisi ini bisa menandakan adanya penyakit atau kondisi tertentu di dalam tubuh. ', '0.0', 'https://www.alodokter.com/demam'),

  ('22', 'Infeksi Saluran Kemih', 'Infeksi saluran kemih adalah kondisi ketika organ yang termasuk ke dalam sistem kemih mengalami infeksi. Organ tersebut bisa ginjal, ureter, uretra, atau kandung kemih.', '48.7', 'https://www.alodokter.com/infeksi-saluran-kemih'),

  ('23', 'Leukimia', 'Leukemia adalah kanker darah akibat tubuh terlalu banyak memproduksi sel darah putih abnormal. Leukemia dapat terjadi pada orang dewasa dan anak-anak.', '78.9', 'https://www.alodokter.com/leukemia'),

  ('24', 'Kanker Kulit Melanoma', 'Kanker kulit melanoma adalah kanker kulit yang berkembang dari melanosit. Selain di kulit, melanoma juga dapat muncul di mata.', '78.0', 'https://www.alodokter.com/kanker-kulit-melanoma');
}

